    <script>

        //صدای بوق
        function beep() {
            var snd = new Audio("data:audio/wav;base64,");
            snd.play();
        }

        let page = 1;
        let scrolling=false;
        $(window).scroll(function () {

            console.log("curent=" + $(document).scrollTop());
            console.log($(document).height() - $(window).height());
            if ($(window).height() + $(document).scrollTop() +100 >= $(document).height()&& scrolling === false){
                scrolling =true;
                $.ajax({
                    type: "GET",
                    url: "/insertScrollNews/" + page,
                    success: function (data) {
                        page = page + 1;
                        $("body").append(data);
                        scrolling=false;
                        /*console.log(element)
                        $(element).animate({backgroundColor: "#eeeeee"});*/
                    },
                    error: function () {
                        scrolling=false;
                    }
                });
            }
        });


        $(document).ready(function () {
            var conn = new ReconnectingWebSocket('ws://82.115.16.178:1020');
            conn.onopen = function (e) {
                console.log("Connection stablished");
            }
            conn.onmessage = function (e) {
                //console.log('newsMessage');
                message = e.data;
                if (message == "new") {
                    $.ajax({
                        type: "GET",
                        url: "/addNewNewsBlock",
                        success: function (data) {
                            //console.log('newsMessage');
                            //location.reload()
                            //document.getElementById(newsID).innerHTML=data;

                            $("#news").prepend(data);
                            //$(divID).replaceWith(data);
                            /*console.log(element)
                            $(element).animate({backgroundColor: "#eeeeee"});*/
                        }
                    });
                } else {
                    var newsID = e.data
                    var divID = "#" + newsID;
                    $.ajax({
                        type: "GET",
                        url: "/singleBlockNews/" + newsID,
                        success: function (data) {
                            //console.log('newsMessage');
                            //location.reload()
                            //document.getElementById(newsID).innerHTML=data;
                            $(divID).replaceWith(data);
                            /*console.log(element)
                            $(element).animate({backgroundColor: "#eeeeee"});*/
                        }
                    });
                }

            }
        });

    </script>
